import Option from '../option/Option'
import './Dropdown.css'
function Dropdown({values,name,style,...rest}) {
       
    return (
        <div>
        <label>{name}</label><br/>
            <select className={`dropdown`}  width={width}>
                {values.map((value)=>(<Option value={value}></Option>))}
            </select>
        </div>
    )
}

export default Dropdown
