import React,{useState,useRef} from 'react'
import Button from '../button/Button'
import Dropdown from '../dropdown/Dropdown'
import TextInput from '../textInput/TextInput'
import './Form.css'

function Form({name}) {
   
    const tests=[{
        name:'Tested Biomarker',
        values: ['ECG', 'Glucose', 'CBC']
    },{
        name:'unit',
        values:['mgdl','mg','cm']
    }

]
const [countRows,setCountRows]=useState(1);
const submitRef=useRef();
const refRow=useRef(null);
const labResults=useRef(new Map());



//////////////////////////
const submitHandler=(e)=>{
   console.log(e.currentTarget);  
}
/////////////////////////////////
const saveHandler=(e)=>{ 
console.log(e.target);
// e.target.disabled=true;
// e.target.style.opacity=0.5;
}
///////////////////////////////
const eventBubbling=(e)=>{
 
  console.log(e.target.type)
  if(e.target.type==='submit'){
      console.log(e.currentTarget.children);
      refRow.current = e.currentTarget;
      let rowElements = refRow.current.children;
      var l = rowElements.length;
      let selectedTest = rowElements[2].value;
      let selectedUnit=rowElements[5].value;
      let defaultValue='Select';
      let selectedValue=rowElements[l-2].value;
      let options = rowElements[2].children;
      Array.from(options).forEach((option) => {
          if (option.value === selectedTest) {
              option.disabled = true;
              option.opacity = 0.5;
          }
      })
      rowElements[l - 2].value='';
      if(selectedTest!=defaultValue && selectedUnit!=defaultValue && selectedValue.trim()!=''){
          labResults.current.set(selectedTest, { 'unit': selectedUnit, 'value': selectedValue });
          console.log(labResults);
          setCountRows((prevCount) => prevCount + 1);
          console.log(countRows);
          console.log(e.target)
          if(countRows==rowElements[2].children.length-1){
              e.target.disabled=true;
              e.target.style.opacity='0.5';
              submitRef.current.disabled=false;
              submitRef.current.opacity
          }
      }
      else{
          alert('invalid entry');
      }
         
        //    if(countRows===l){
        //          refRow.current.children[l-1].disabled=true;
        //          refRow.current.children[l-1].opacity=0.5;
        //    }

  }   
}
//////////////////////
const editHandler=(e)=>{
    console.log(e.target);
}

    return (
        <div className={`container-fluid `} id="lab-report" >  
           
            <div className={`grid form`} >
            <div className='row'>
                    <h3 >{name}</h3>
            </div>
       
                <div className='row'  onClick={(e) => eventBubbling(e)} >
                    {tests.map((test) => (<Dropdown key={tests.indexOf(test)} name={test.name} values={test.values} changeHandler={(e) => null} className='col' defaultValue='Select'/>))}
                    <TextInput name="result" changeHandler={(e) => null} style={{ backgroundColor: 'aliceBlue' }} className='col' />
                    <Button name='Save' clickHandler={saveHandler} disabled={false} variant='primary' color='white' className='col' ></Button>
                    </div>
               
                <div className='row flex-end'>
                    <Button name='Submit' clickHandler={submitHandler} variant='secondary' color='white'
                    className='col' disabled={true} ref={submitRef}></Button>
                  
                </div>

            </div>
            
        </div>
    )
}

export default Form
