import React,{useState,useRef} from 'react'
import Button from '../button/Button'
import Dropdown from '../dropdown/Dropdown'
import TextInput from '../textInput/TextInput'
import './Form.css'

function Form({name}) {
   
    const tests=[{
        name:'Tested Biomarker',
        values: ['ECG', 'Glucose', 'CBC']
    },{
        name:'unit',
        values:['mgdl','mg','cm']
    }

]
const keys=['ECG','Glucose','CBC']
const formRef=useRef();
const refRow=useRef(null);
const refRows=useRef();

const[labResults,setLabResults]=useState([]);



//////////////////////////
const addRowHandler=(e)=>{
   console.log(e.currentTarget);  
}
/////////////////////////////////
const saveHandler=(e)=>{ 
console.log(e.target.disabled);
e.target.disabled=true;
e.target.style.opacity=0.5;
}
///////////////////////////////
const eventBubbling=(e)=>{
  console.log(e.currentTarget);
  console.log(e.target.type)
  if(e.target.type==='submit'){
      refRow.current = e.currentTarget;
  //console.log(refRow.current);
    let l = refRow.current.children.length;
    console.log(refRow.current.children[2].children);
        let editButton = refRow.current.children[l - 1];
    editButton.disabled=false;
    editButton.style.opacity=1;
      let selectedVal = refRow.current.children[2].value;
     let options = refRow.current.children[2].children;
      Array.from(options).forEach((option)=>{
          if(option.value===selectedVal){
              option.disabled=true;
              option.opacity=0.5;
          }
      })
        // console.log(tests.current)
        // if(!refRows.current.contains(selectedVal)){
        //     const newRef=useRef()
        //     refRows.current.set(selectedVal,newRef);
        // }
        //    console.log(refRows.current);
  }   
}
//////////////////////
const editHandler=(e)=>{
    console.log(e.target);
}

    return (
        <div className={`container-fluid `} id="lab-report" >  
           
            <div className={`grid form`} ref={formRef}>
            <div className='row'>
                    <h3 >{name}</h3>
            </div>
            
            {keys.map((key)=>
               ( <div className='row' key={key} onClick={(e)=>eventBubbling(e)}>
                { tests.map((test) => (<Dropdown key={tests.indexOf(test)} name={test.name} values={test. values} changeHandler={(e) => null} className='col' />))}
                    <TextInput name="result" changeHandler={(e)=>null} style={{ backgroundColor: 'aliceBlue' }} className='col' />
                    <Button name='Save' clickHandler={saveHandler} disabled={false} variant='primary' color='white' className='col' ></Button>
                <Button name='Edit' clickHandler={editHandler} disabled={true} variant='primary' color='white' className='col' ></Button>
                </div>
               )
            )}
               
                <div className='row flex-end'>
                    <Button name='Add Row' clickHandler={addRowHandler} variant='secondary' color='white'
                    className='col'></Button>
                  
                </div>

            </div>
            
        </div>
    )
}

export default Form
