import React from 'react'
import Button from '../button/Button'
import Dropdown from '../dropdown/Dropdown'
import TextInput from '../textInput/TextInput'
import './Form.css'

function Form({name}) {
    const tests=[{
        name:'Tested Biomarker',
        values: ['ECG', 'Glucose', 'CBC']
    },{
        name:'unit',
        values:['mgdl','mg','cm']
    }

]

const selectHandler=(e)=>{
   console.log(e.target.value);
}

const inputHandler=(e)=>{
   console.log(e.target.value);
}
const rowHandler=(e)=>{
  console.log(e.target);
}
const saveHandler=(e)=>{
  console.log(e.target);
}
    return (
        <div className={`container-fluid`}>        
            <div className={``}>
            <div className='row'>
                    <h3 >{name}</h3>
            </div>
                <div className='row'>
                    {tests.map(( test) => (<Dropdown key={tests.indexOf(test)} name={test.name} values={test.values} changeHandler={selectHandler}/>))}
                    <TextInput name="result" changeHandler={inputHandler} />
                </div>
                <div className='row'>
                    <Button name='Add Row' clickHandler={rowHandler} variant='secondary' color='white'></Button>
                    <Button name='Save' clickHandler={saveHandler} variant='primary' color='white'></Button>
                </div>

            </div>
            
        </div>
    )
}

export default Form
