import React from 'react'
import Option from '../option/Option'
import './Dropdown.css'
function Dropdown({values,name,style,changeHandler,defaultValue, ...rest}) {
       
    return (
        <>
        <label>{name}</label><br/>
            <select className={`dropdown`} style={style} onChange={(e) => changeHandler(e)} selected={defaultValue}>
                <Option disabled={true}  value="Select"></Option>
                {values.map((value)=>(<Option value={value} key={values.indexOf(value)} disabled={false}></Option>))}
            </select>
        </>
    )
}

export default Dropdown
